INSERT INTO `ec_products_multilingual` VALUES ('975', '497', '1', 'VC70N0', '<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Le terminal embarqu&eacute; VC70N0&nbsp;combine une architecture mat&eacute;rielle&nbsp;<span style=\"color:rgb(0, 0, 128)\"><strong>robuste&nbsp;</strong></span>avec des hautes performances de communication&nbsp;<span style=\"color:rgb(0, 0, 128)\"><strong>Wifi</strong></span>.&nbsp;</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(92, 94, 96)\">De </span><strong><span style=\"color:#000080\">conception compacte</span></strong><span style=\"color:rgb(92, 94, 96)\">, il garantit une visibilit&eacute; et une s&eacute;curit&eacute; accrues et, gr&acirc;ce &agrave; son </span><strong><span style=\"color:#000080\">grand &eacute;cran haute r&eacute;solution</span></strong><span style=\"color:rgb(92, 94, 96)\">, il permet d&#39;afficher toutes les informations n&eacute;cessaires aux op&eacute;rateurs pour am&eacute;liorer la </span>productivit&eacute; <span style=\"color:rgb(92, 94, 96)\">et la pr&eacute;cision des t&acirc;ches dans presque toutes les conditions d&#39;&eacute;clairage.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif; font-size:14px\">Ce mat&eacute;riel est&nbsp;</span><span style=\"color:rgb(0, 0, 128); font-family:arial,helvetica,sans-serif; font-size:14px\"><strong>disponible</strong></span><span style=\"font-family:arial,helvetica,sans-serif; font-size:14px\">&nbsp;chez Eurocomputer.</span></p>\r\n', '0', '0', 'Motorola-VC70N0.pdf', '<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">scanner&nbsp;</span>VC70N0</p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">lecteur&nbsp;</span>VC70N0</p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">terminal&nbsp;</span>VC70N0</p>\r\n\r\n<p><span style=\"font-family