INSERT INTO `ec_products_multilingual` VALUES ('983', '501', '1', 'TC8000', '<p style=\"text-align:justify\">De par sa conception novatrice, ce tout nouvel ordinateur portable d&eacute;livre des <strong><span style=\"color:#000080\">gains de productivit&eacute; </span></strong>r&eacute;volutionnaires en entrep&ocirc;t.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Gr&acirc;ce &agrave; un angle de lecture novateur qui &eacute;vite &agrave; vos employ&eacute;s d&#39;incliner des milliers de fois leur appareil pour visualiser l&#39;&eacute;cran, le <span style=\"color:#000080\"><strong>TC8000 r&eacute;duit les mouvements inutiles</strong></span>.</p>\r\n\r\n<p style=\"text-align:justify\">Autre innovation pour un appareil portatif, ce terminal mobile prend en charge des fonctionnalit&eacute;s de lecture souples en mode mains libres ou portatif, qui dynamisent plus encore la <strong><span style=\"color:#000080\">productivit&eacute;</span></strong>.</p>\r\n\r\n<p style=\"text-align:justify\">Ces &eacute;crans extr&ecirc;mement <strong><span style=\"color:#000080\">intuitifs </span></strong>assurent une r&eacute;duction consid&eacute;rable du nombre d&#39;interactions requis pour effectuer une t&acirc;che, sans programmation ni modification de l&#39;application h&ocirc;te. R&eacute;sultat&nbsp;: des t&acirc;ches ex&eacute;cut&eacute;es plus rapidement et une productivit&eacute; accrue.&nbsp;</p>\r\n', '0', '0', 'tc8000-specification-sheet-fr-emea.pdf', '<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">scanner&nbsp;</span>TC8000</p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">lecteur</span>TC8000<span style=\"font-family:arial,sans-serif; font-size:10pt\"> </span></p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">terminal&nbsp;</span>TC8000</p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">douchette&nbsp;</span>TC8000</p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font