INSERT INTO `ec_products_multilingual` VALUES ('997', '508', '1', 'Gryphon GBT4430 2D', '<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Le lecteur de code barre Datalogic GBT4430 <span style=\"color:#000080\"><strong>2D </strong></span>dot&eacute; d&#39;une communication&nbsp;<span style=\"color:#000080\"><strong>bluetooth</strong></span>&nbsp;sans fil.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Avec un syst&egrave;me de scan pr&eacute;cis gr&acirc;ce &agrave; son aide &agrave; la vis&eacute;e &quot;<span style=\"color:#000080\"><strong>4 points</strong></span>&quot; et &agrave; son syst&egrave;me de validation des scans &quot;<span style=\"color:#000080\"><strong>Green Spot</strong></span>&quot; Datalogic.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Le mode <span style=\"color:#000080\"><strong>Batch</strong></span> permet de sauvegarder 1.200 codes.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Livr&eacute; en <span style=\"color:#000080\"><strong>kit complet</strong></span> avec son c&acirc;ble USB, son chargeur et son stand/base de rechargement, il est utilisable <span style=\"color:#000080\"><strong>en manuel</strong></span> ou en&nbsp;<span style=\"color:#000080\"><strong>scanner de pr&eacute;sentation.</strong></span></span></span></p>\r\n', '0', '0', 'Datalogic_GBT4430.pdf', '<p><span style=\"color:rgb(95, 96, 98); font-family:arial,helvetica,sans-serif; font-size:11px\">GBT4400-BK-BTK00 /&nbsp;</span><span style=\"color:rgb(95, 96, 98); font-family:arial,helvetica,sans-serif; font-size:11px\">GBT4400-WH-BTK00</span></p>\r\n\r\n<p><span style=\"color:rgb(95, 96, 98); font-family:arial,helvetica,sans-serif; font-size:11px\">GBT