INSERT INTO `ec_products_multilingual` VALUES ('999', '509', '1', 'Z90', '<p>Polyvalent compact et efficace, ce client l&eacute;ger, il offre une <span style=\"color:#000080\"><strong>rapidit&eacute; d&#39;ex&eacute;cution </strong></span>gr&acirc;ce &agrave; l&#39;interface SuperSpeed USB 3.0.</p>\r\n\r\n<p>Il comprend &eacute;galement un processeur sui regroupe les fonctions des processeur central, d&#39;une <span style=\"color:#000080\"><strong>puce graphique</strong></span> et d&#39;un d&eacute;codeur vid&eacute;o qui acc&eacute;l&egrave;re le <span style=\"color:#000080\"><strong>d&eacute;codage HD</strong></span>.</p>\r\n\r\n<p>Il est dot&eacute; de plusieurs ports <span style=\"color:#000080\"><strong>USB</strong></span>, et propose des optins d&#39;affichage riche et p&eacute;rennne notamment avec des conencteurs <span style=\"color:#000080\"><strong>DisplayPort</strong></span> et <span style=\"color:#000080\">DVI</span>.&nbsp;</p>\r\n\r\n<p>Le V90D est propos&eacute; sur <span style=\"color:#000080\"><strong>Windows embedded</strong></span> qui offre une grande flexibilit&eacute;.&nbsp;</p>\r\n', '0', '0', 'client-leger-dell-wyse-z90d8-16gf-4gr-dual-core.jpg,Wyse-Z-class.pdf', '<p>90975202L</p>\r\n\r\n<p>client leger Z90</p>\r\n\r\n<p>client l&eacute;ger Z90</p>\r\n\r\n<p>platine Z90</p>\r\n\r\n<p>platine Wyse Z90</p>\r\n', '', '', '', '', '', '', '', '218', '', '16G/2G', 'USB,Ethernet, Display port,VGA,DVI', 'en option ', 'Windows embedded ', '215x69x225 mm', '1.59 kg', 'CLIENTS LÉGERS DELL WYSE Z90', '<p>90975202L</p>\r\n\r\n<p><strong><a href=\"https://www.eurocomputer.fr/micro/clients-legers-dell-wyse/clients-legers/3010-491.html\">Client leger dell wyse&nbsp;3010</a></strong></p>\r\n\r\n<p><strong><a href=\"https://www.eurocomputer.fr/micro/clients-legers-dell-wyse/clients-legers/3020-492.html\">Client leger dell wyse 3020</a></strong></p>\r\n\r\n<p><strong><a href=\"https://www.eurocomputer.fr/micro/clients-legers-dell-wyse/clients-legers/3030-490.htm