INSERT INTO `ec_products_multilingual` VALUES ('1001', '510', '1', 'M402', '<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(44, 44, 44)\">L&#39;imprimante laser HP M402&nbsp;est une imprimante laser monochrome.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Cette imprimante sort du mode veille et imprime plus rapidement que n&#39;importe quelle autre imprimante de sa cat&eacute;gorie.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(44, 44, 44)\">Pr&eacute;vue pour des volumes mensuels allant de&nbsp;</span><span style=\"color:rgb(0, 0, 128)\"><strong>750&nbsp;&agrave; 4000 pages</strong></span><span style=\"color:rgb(44, 44, 44)\">, elle promet une qualit&eacute; professionnelle irr&eacute;prochable, une&nbsp;</span><span style=\"color:rgb(0, 0, 128)\"><strong>grande facilit&eacute; d&#39;utilisation</strong></span><span style=\"color:rgb(44, 44, 44)\">&nbsp;et une&nbsp;</span><span style=\"color:rgb(0, 0, 128)\"><strong>&eacute;conomie d&#39;&eacute;nergie</strong></span><span style=\"color:rgb(44, 44, 44)\">&nbsp;avec notamment une fonction qui met l&#39;imprimante sous tension lorsque vous en avez besoin et l&#39;&eacute;teint lorsque vous n&#39;en avez plus besoin.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Rapide elle &eacute;dite jusqu&#39;&agrave; 38&nbsp;pages par minute.</span></span></p>\r\n', '0', '0', '', '<p>C5F94A</p>\r\n\r\n<p>C5J91A</p>\r\n\r\n<p>C5F93A&nbsp;</p>\r\n\r\n<p>imprimante HP M402</p>\r\n', '', '', '', '', '', '', '', '236;237', '351;353;354;356;366', '38ppm', 'monochrome', 'A4', '900 feuilles', '38.1x35.7x21.6 cm', 'petit groupe de travail', 'HP M402', '<p>C5F94A</p>\r\n\r\n<p>C5J91A</p>\r\n\r\n<p>C5F93A&nbsp;</p>\r\n\r\n<p><a href=\"https://www.eurocomputer.fr/imprimantes-laser/hp/petit-groupe-de-