INSERT INTO `ec_products_multilingual` VALUES ('1007', '513', '1', 'MC67', '<p style=\"text-align:justify\">Le terminal <span style=\"color:#000080\"><strong>mobile MC67</strong></span> de la marque Zebra / Motorola est un PDA permettant de travailler rapidement et int&eacute;lligemment.</p>\r\n\r\n<p style=\"text-align:justify\">Il est dot&eacute; de connexions cellulaires haut d&eacute;bit et Wi-Fi sans fil <span style=\"color:#000080\"><strong>HSPA+ 4G</strong></span>, 802.11a/b/g/n, Bluetooth 2.1 EDR et GPS.</p>\r\n\r\n<p style=\"text-align:justify\">Le terminal <span style=\"color:#000080\"><strong>se g&egrave;re lui m&ecirc;me</strong></span>, les capteurs int&eacute;gr&eacute;s &eacute;teignent automatiquement le MC67 quand il n&#39;est pas utilis&eacute; et ajuste la luminosit&eacute; de l&#39;&eacute;cran en fonction de l&#39;&eacute;clairage.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Son format <span style=\"color:#000080\"><strong>compact</strong></span> et <span style=\"color:#000080\"><strong>robuste</strong></span> est id&eacute;al pour le&nbsp;personnel de terrain.</p>\r\n\r\n<p style=\"text-align:justify\">Dot&eacute; &eacute;galement d&#39;un <span style=\"color:#000080\"><strong>&eacute;cran lumineux</strong></span> et d&#39;une <span style=\"color:#000080\"><strong>technologie de lecteur avanc&eacute;e</strong></span>, il est tr&eacute;s facile d&#39;utilisation. En effet il lit les code-barres <span style=\"color:#000080\"><strong>1D</strong></span> et <strong><span style=\"color:#000080\">2D</span></strong> rapidement m&ecirc;me si ces <span style=\"color:#000080\"><strong>codes sont mal imprim&eacute;s, sales ou d&eacute;t&eacute;rior&eacute;s.&nbsp;</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Vous serez &eacute;galement surpris par sa <span style=\"color:#000080\"><strong>communication vocale</strong></span> parfaitement audible, l&#39;appareil est &eacute;galement &eacute;quip&eacute; d&#39;un <span style=\"color:#000080\"><strong>a<strong>pp</strong>areil photo</strong></span>.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', '0', '0', 'Motorola Zebra - MC67.pdf', '<p><strong>MC67NA-PDABAB00500</strong></p>\r\n\r\n<p><strong>MC67NA-PDADAA00500</strong></p>\r\n\r\n<p><strong>MC67NA-PDADAB00500</strong></p>\r\n\r\n<p><strong>MC67NA-PDABFD00500</strong></p>\r\n\r\n<p><strong>MC67NA-PDABAF00500</strong></p>\r\n\r\n<p><strong>MC67NA-PDADAF00500</strong></p>\r\n\r\n<p><strong>MC67NA-PDABGC00500</strong></p>\r\n\r\n<p><strong>MC67NA-PDABAA00500</strong></p>\r\n\r\n<p><strong>MC67NA-PDADGC00500</strong></p>\r\n\r\n<p><strong>MC67NA-PDAEAA00500</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>scanner MC67</strong></p>\r\n\r\n<p><strong>lecteur MC67</strong></p>\r\n\r\n<p><strong>terminal MC67</strong></p>\r\n\r\n<p><strong>douchette MC67</strong></p>\r\n\r\n<p><strong>scanner code-barres MC67</strong></p>\r\n\r\n<p><strong>lecteur code-barres&nbsp;&nbsp;MC67</strong></p>\r\n\r\n<p><strong>terminal code-barres&nbsp;MC67</strong></p>\r\n\r\n<p><strong>douchette code-barres&nbsp;MC67</strong></p>\r\n', '', '', '', '', '', '', '', '456;148;514', '344;345;346;347;348', 'Windows Embedded Handheld 6.5 Pro ou Android 4.1', '3,5 pouces (8,89 cm)', '1D / 2D', 'Numérique', '162,2 x 77 x 33,5 mm', '385 g', 'Motorola / Symbol MC67', '<p><strong>MC67NA-PDABAB00500</strong></p>\r\n\r\n<p><strong>MC67NA-PDADAA00500</strong></p>\r\n\r\n<p><strong>MC67NA-PDADAB00500</strong></p>\r\n\r\n<p><strong>MC67NA-PDABFD00500</strong></p>\r\n\r\n<p><strong>MC67NA-PDABAF00500</strong></p>\r\n\r\n<p><strong>MC67NA-PDADAF00500</strong></p>\r\n\r\n<p><strong>MC67NA-PDABGC00500</strong></p>\r\n\r\n<p><strong>MC67NA-PDABAA00500</strong></p>\r\n\r\n<p><strong>MC67NA-PDADGC00500</strong></p>\r\n\r\n<p><strong>MC67NA-PDAEAA00500</strong></p>\r\n\r\n<p><a href=\"https://www.eurocomputer.fr/terminaux-code-barres-embarques/motorola-symbol/terminal-mobile-pda/mc75-514.html\"><strong>Motorola/symbol MC75</strong></a></p>\r\n\r\n<p><a href=\