INSERT INTO `ec_products_multilingual` VALUES ('1009', '514', '1', 'MC75', '<p style=\"text-align:justify\"><span style=\"font-size:14px\">Le <span style=\"color:#000080\"><strong>MC75</strong></span> est un terminal mobile PDA de la marque Zebra / Motorola, aussi appel&eacute; assistant num&eacute;rique, il est tr&eacute;s <span style=\"color:#000080\"><strong>robuste</strong></span> puisqu&#39;il a &eacute;t&eacute; con&ccedil;u pour <span style=\"color:#000080\"><strong>r&eacute;sister aux usages </strong></span>quotidien de l&#39;entreprise.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Le PDA&nbsp;MC75 est <span style=\"color:#000080\"><strong>simple d&#39;utilisation</strong></span> gr&acirc;ce &agrave; son&nbsp;format </span><span style=\"color:rgb(0, 0, 128); font-size:14px\"><strong>compact</strong></span><span style=\"font-size:14px\"> et son </span><strong><span style=\"color:#000080\">&eacute;cran lumineux.</span></strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Il <span style=\"color:#000080\"><strong>lit les code-barres</strong></span> tr&eacute;s rapidement, <strong><span style=\"color:#000080\">m&ecirc;me si les codes sont peu lisibles ou sales</span></strong>.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Il est &eacute;galement dot&eacute; de connexions cellulaires haut d&eacute;bit et Wi-Fi sans fil&nbsp;</span><span style=\"color:#000080\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>HSPA 3.5G</strong></span></span><span style=\"font-family:arial,helvetica,sans-serif\">, 802.11a/b/g/n, Bluetooth 2.1 EDR et GPS.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:14px\">Une des <span style=\"color:#000080\"><strong>sp&eacute;cificit&eacute;s de ce scanphone</strong></span> se trouve dans son clavier compos&eacute; de <span style=\"color:#000080\"><strong>5 touches fonction</strong></span></span>.</span></span></p>\r\n', '0', '0', 'Motorola-MC75.pdf', '<p><strong>MC75A6-P4CSWQRHF01</strong></p>\r\n\r\n<p><strong>MC75A0-P80SWRQA904</strong></p>\r\n\r\n<p><strong>MC75A0-P40SWQQJ9JP</strong></p>\r\n\r\n<p><strong>scanner MC75</strong></p>\r\n\r\n<p><strong>lecteur MC75</strong></p>\r\n\r\n<p><strong>terminal MC75</strong></p>\r\n\r\n<p><strong>douchette MC75</strong></p>\r\n\r\n<p><strong>scanner code-barres MC75</strong></p>\r\n\r\n<p><strong>lecteur code-barres&nbsp;&nbsp;MC75</strong></p>\r\n\r\n<p><strong>terminal code-barres&nbsp;MC75</strong></p>\r\n\r\n<p><strong>douchette code-barres&nbsp;MC75</strong></p>\r\n', '', '', '', '', '', '', '', '456;148;513', '344;345;346;347;348', 'Windows Embedded Handheld ', '3.5 pouces (8.89 cm)', '1D / 2D', 'Numérique ', '152.4 x 84 x 44 mm', '398 g', 'Motorola / Symbol MC75', '<p><strong>MC75A6-P4CSWQRHF01</strong></p>\r\n\r\n<p><strong>MC75A0-P80SWRQA904</strong></p>\r\n\r\n<p><strong>MC75A0-P40SWQQJ9JP</strong></p>\r\n\r\n<p><a href=\"https://www.eurocomputer.fr/terminaux-code-barres-embarques/motorola-symbol/terminal-mobile-pda/mc67-513.html\"><strong>Motorola/symbol MC67</strong></a></p>\r\n\r\n<p><a href=\"https://www.eurocomputer.fr/terminaux-code-barres-embarques/motorola-symbol/terminal-mobile-pda/mc45-456.html\">Motorola/symbol MC45</a></p>\r\n\r\n<p><a href=\"https://www.eurocomputer.fr/terminaux-code-barres-embarques/motorola-symbol/terminal-mobile-pda/mc55n0-mc55a0-148.html\">Motorola/symbol MC55N0&nbsp;/ MC55A0</a></p>\r\n\r\n<p>&nbsp;</p>\r\n');
