INSERT INTO `ec_products_multilingual` VALUES ('1015', '517', '1', 'DS4308 2D', '<p style=\"text-align:justify\">Le scanner code-barre, <span style=\"color:#000080\"><strong>DS4308</strong></span> est un des lecteurs les plus perfomants, en effet sa <span style=\"color:#000080\"><strong>port&eacute;e de lecture</strong></span> est aussi efficace&nbsp;<span style=\"color:#000080\"><strong>de loin que de pr&eacute;s</strong></span>.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Tout types des code-barres peuvent &ecirc;tre scann&eacute;s par le lecteur qu&#39;ils soient <span style=\"color:#000080\"><strong>1D</strong></span> ou <span style=\"color:#000080\"><strong>2D</strong></span>.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Gr&acirc;ce &agrave; la<span style=\"color:#000080\"><strong> technologie PRZM</strong></span>&nbsp;le lecteur est extr&ecirc;mement rapide, il fera gagner beaucoup de temps &agrave; vos employ&eacute;s.</p>\r\n\r\n<p style=\"text-align:justify\">En plus d&#39;&ecirc;tre polyvalent le scanner code-barre DS4308 est tr&eacute;s robuste puisqu&#39;il peut r&eacute;sister &agrave; une chute de 1m83.</p>\r\n\r\n<p>&nbsp;</p>\r\n', '0', '0', 'Motorola DS4308-fr.pdf', '<p>DS4308-SR00006ZZWW</p>\r\n\r\n<p>DS4308-SR00007ZZWW</p>\r\n\r\n<p>DS4308-SR00007PZWW</p>\r\n\r\n<p>DS4308-SR6U2100AZWDS4308-SR7U2100AZW</p>\r\n\r\n<p>DS4308-SR6U2100SGWDS4308-SR7U2100SGW</p>\r\n\r\n<p>DS4308-SR7U2100PZW</p>\r\n\r\n<p>DS4308-HD00007ZZWW</p>\r\n\r\n<p>DS4308-HD7U2100AZW</p>\r\n\r\n<p>DS4308-HD7U2100SGW</p>\r\n\r\n<p>DS4308-SR00007PCWW</p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">scanner DS4308</span></p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">lecteur&nbsp;</span><span style=\"font-family:arial,sans-serif; font-size:13.3333px\">DS4308</span></p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">terminal&nbsp;</span><span style=\"font-family:arial,sans-serif; font-size:13.3333px\"