INSERT INTO `ec_products_multilingual` VALUES ('1025', '522', '1', '3820 Bluetooth ', '<p style=\"text-align:justify\">Le <span style=\"color:#000080\"><strong>scanner code-barres Honeywell 3820</strong></span>, est une <span style=\"color:#000080\"><strong>douchette</strong></span>&nbsp;adapt&eacute;e &agrave; tous les secteurs d&#39;activit&eacute;. &nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Dot&eacute;e d&#39;une technologie <strong><span style=\"color:#000080\">bluetooth sans-fil</span></strong> elle permettra &agrave; vos utilisateurs une totale<strong><span style=\"color:#000080\"> libert&eacute; de mouvement.&nbsp;</span></strong></p>\r\n\r\n<p style=\"text-align:justify\">La distance de lecture de la <span style=\"color:#000080\"><strong>douchette 3820</strong></span> s&#39;&eacute;tend jusqu&#39;&agrave; <span style=\"color:#000080\"><strong>61 cm.&nbsp;</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Le<span style=\"color:#000080\"><strong> l<strong>ec</strong>teur code-barres Honeywell 3820</strong></span> peut lire des codes <span style=\"color:#000080\"><strong>1D et GS1 Databar Standard.&nbsp;</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Les principaux atouts de ce <span style=\"color:#000080\"><strong>scanner code-barres Honeywell 3820</strong></span> est sa <span style=\"color:#000080\"><strong>robustesse</strong></span> puisqu&#39;il peut r&eacute;sister &agrave; une <span style=\"color:#000080\"><strong>chute de 1,80 m&egrave;tres</strong></span> ainsi que sa vitesse de scan&nbsp;allant jusqu&agrave; <span style=\"color:#000080\"><strong>270 lectures par secondes</strong></span>.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n', '0', '0', '3820.jpg,Honeywell-3820-sansfil.pdf', '<p>3820SR0C0B-0FA0E</p>\r\n\r\n<p>3820SR0C0B-0GA0E</p>\r\n\r\n<p>3820SR0C0B-0IA0E&nbsp;</p>\r\n\r\n<p>3820SR0C0BE</p>\r\n\r\n<p>3820SR0C0B-0FB0E</p>\r\n\r\n<p>3820SR0C0B-0GB0E</p>\r\n\r\n<p>Douchette 3820i</p>\r\n\r\n<p>Honeywell 382